import React, {useState, useEffect }from 'react'
import firebase from '../../firebase-config'

const UpdateDatabase =({inputedUserId})=>{
    const [userID,setUserID] = useState([])
    const [timestamp, setTimestamp] = useState([])
    addUser();
    function addUser(){
        setUserID(inputedUserId);
        setTimestamp(Date())
        firebase
        .firestore()
        .collection('users')
        .add({
            userID,
            timestamp
        })
        .then(()=>{
            setUserID('')
            setTimestamp('')
        })
    }

    return (
        <div>
            updated
        </div>
    )
}

export default UpdateDatabase;
