import React, {useState, useEffect }from 'react'
import firebase from '../../firebase-config'
import TimeStamp from 'react-timestamp'


function GetInfoFromUser(uid) {
    const [users,setUsers] = useState([]);     

    useEffect(()=>{


        const unsubscribe = firebase
        .firestore()
            .collection('users')
            .where('userID', '==', uid)
            .onSnapshot((snapshot)=>{
            const newUsers = snapshot.docs.map((doc)=> ({
                id: doc.id,
                ...doc.data()

            }))
            setUsers(newUsers);
            console.log('12d', newUsers)
        }) 
        return () => unsubscribe()
        ()
    },[])

        return users;
}

const GetFromDatabase =({userUid})=>{
    console.log(userUid)
    const users = GetInfoFromUser(userUid);



    return (
      <div>

          {users.map(user=>(
          <div key={user.id}> 
              <h1>hh</h1>
              {user.userID}
              <TimeStamp date={firebase.auth().currentUser.metadata.lastSignInTime} options={{ includeDay: true, twentyFourHour: true }}/>
              {user.timestamp}
          </div>  
          ))}
      </div>
    )
}
export default GetFromDatabase
