import React, { Component } from "react";
import "./about.scss";
import firebase from '../../firebase-config';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import Database from '../useDatabase/getFromDatabase'
import TimeStamp from 'react-timestamp'
import UpdateDatabase from '../useDatabase/updateDatabase'
import ReactDOM from 'react-dom';


class About extends Component {
    state = {isSignedIn:false}

    uiConfig ={
        signInFlow: 'popup',
        signInOptions: [
            firebase.auth.GithubAuthProvider.PROVIDER_ID,
            firebase.auth.FacebookAuthProvider.PROVIDER_ID
        ],
        callbacks: {
            signInSuccess: () => false
        }
    }

    componentDidMount = () => {

        firebase.auth().onAuthStateChanged(user=>{
            this.setState({isSignedIn:!!user})
            console.log("user",user)
        })
    }

      render() {
        /** note about database. userid|latest logged in| time when logged in|  */
        //

    return(
        <section>

            <div className="grid-container">

           {
           this.state.isSignedIn
           ? 
           <div className="signedIn-grid">
                <div className="user-info">
                    <img src={firebase.auth().currentUser.photoURL} alt={firebase.auth().currentUser.name}/>
                    <div className="currentUserInfo">
                    <Database/>
                        <h2> {firebase.auth().currentUser.displayName}</h2>
                        <p>Senast inloggad: 
                        <UpdateDatabase inputedUserId={firebase.auth().currentUser.uid} />     
                        <TimeStamp date={firebase.auth().currentUser.metadata.lastSignInTime} options={{ includeDay: true, twentyFourHour: true }}/> </p>
                    </div>
                </div>

            <div className="right-div"><div className="sign-out-button" onClick ={()=>firebase.auth().signOut()}> sign out</div> </div>
         </div>

           : 
           <div className="logInBoxes">
               <StyledFirebaseAuth
           uiConfig={this.uiConfig}
           firebaseAuth={firebase.auth()}  /> 
           </div>
            }
    </div>

        </section>
    )
    }

}
ReactDOM.render(
    <About />,
    document.getElementById('root')
  );
export default About;
