import React, {useState, useEffect }from 'react'
import firebase from '../../firebase-config'


const GetFromDatabase =({userId})=>{
    const users = getInfoFromUser(); 

    function getInfoFromUser(){
        const [users,setUsers] = useState([]);

        useEffect(()=>{
            firebase.firestore()
                .collection('users')
                .onSnapshot((snapshot)=>{
                    debugger

                const newUsers = snapshot.docs.map((doc)=> ({
                    id: doc.id,
                    ...doc.data()

                }))
                setUsers(newUsers);
            }) 
        })

            return users;
    }


    return (
      <div>
          {users.map((user)=>{
              <div key={user.id}></div>
          })}
      </div>
    )
}
export default GetFromDatabase
