import React, {useState, useEffect }from 'react'
import firebase from '../../firebase-config'

function GetInfoFromUser() {
    const [users,setUsers] = useState([]);     

    useEffect(()=>{
        firebase.firestore()
            .collection('users')
            .onSnapshot((snapshot)=>{


            const newUsers = snapshot.docs.map((doc)=> ({
                id: doc.id,
                ...doc.data()

            }))
            setUsers(newUsers);
        }) 
    },[])

        return users;
}

const GetFromDatabase =({userId})=>{
    const tusers = GetInfoFromUser();



    return (
      <div>
          {tusers.map((user)=>{
              user.id
          })}
      </div>
    )
}
export default GetFromDatabase
