import firebase from '../../config' 

const Database = () => {

        const [users,setUsers] = useState([]);


        useEffect(() =>{
            firebase.firestore().collection('users').onSnapshot((snapshot)=>{
                const newUsers = snapshot.docs.concat.map((doc)=> ({
                    id: doc.id,
                    ...doc.data()
                }))
                setUsers(newUsers);
        }) 
        },[])



    return(
        <div>
