import React, { Component } from "react";
import "./about.scss";
import firebase from 'firebase';
import {DATABASE_CONFIG} from "../../config"
class About extends Component {
    constructor() {
        super();
        this.About =firebase.initializeApp(DATABASE_CONFIG);
        this.database = this.About.database().ref().child('userID');
        this.state = {
            userID: '',
            timeStamp: '',
        }
    }
      componentDidMount () {
          this.database.on('value', snap =>{
            this.setState ({
                userID: snap.val()
            });
          });

      }
      render() {


    return(
        <section>
            <h1>
             {this.state.userID}   
            </h1>
        </section>

    )
    }
}
export default About;
