import React, {useState, useEffect }from 'react'
import firebase from '../../firebase-config'
import TimeStamp from 'react-timestamp'


function GetInfoFromUser(uid) {
    const [users,setUsers] = useState([]);     

    useEffect(()=>{
        const date = new Date();

        const unsubscribe = firebase
        .firestore()
            .collection('users')
            .where('userID', '==', uid)
            .onSnapshot((snapshot)=>{
            const newUsers = snapshot.docs.map((doc)=> ({
                id: doc.id,
                ...doc.data()
            }))
            setUsers(newUsers);
        })
        return () => unsubscribe()

    },[])

        return users;
}

const GetFromDatabase =({userUid})=>{
    console.log(userUid)
    const users = GetInfoFromUser(userUid);



    return (
      <div>
          {users.map(user=>(
          <div key={user.id}> 
              {user.userID}
              <p>Senast inloggad: <TimeStamp date={user.timestamp} options={{ includeDay: true, twentyFourHour: true }}/> </p>

          </div>  
          ))}
      </div>
    )
}
export default GetFromDatabase
