    const [users,setUsers] = useState([]);     

    useEffect(()=>{
        const date = new Date().getTime();
        const timestamp = Math.floor(date/1000)

        const unsubscribe = firebase
        .firestore()
            .collection('users')
            .where('userID', '==', uid)
            .onSnapshot((snapshot)=>{
            constnewUsers = snapshot.docs.map((doc)=> ({
                id: doc.id,
                ...doc.data()
            }))
            console.log('NEW',newUsers)
            setUsers(newUsers);
        })


      //updates the timestamp in the database  fCvwAqy3yo4kpxToCY0H'
            firebase.firestore()
            .collection('users').doc('fCvwAqy3yo4kpxToCY0H')
            .update({
             timestamp
            })

        console.log('time', timestamp)


        return () => unsubscribe()


    },[])

        return users;
}

const GetFromDatabase =({userUid})=>{
    console.log(userUid)
    const users = GetInfoFromUser(userUid);



    return (
      <div>
          {users.map(user=>(
          <div key={user.id}> 
              {user.id}
              <p>Senast inloggad: <TimeStamp date={user.timestamp} options={{ includeDay: true, twentyFourHour: true }}/> </p>
              {user.timestamp}
          </div>  
          ))}
      </div>
    )
}
export default GetFromDatabase
